mapper.select:
SELECT ID, RSV_DATE, TITLE, RSV_NAME, EMP_NO, START_TIME, END_TIME, DETAIL, MEMO
  FROM RESERVATION
 WHERE to_char(RSV_DATE, 'yyyymmdd') LIKE #{currentMonth}

SELECT TITLE, RSV_NAME, START_TIME, END_TIME, ID, EMP_NO, RSV_DATE
  FROM RESERVATION
 WHERE to_char(RSV_DATE, 'yyyymmdd') = #{reservationDate}

SELECT ID, RSV_DATE, TITLE, RSV_NAME, EMP_NO, START_TIME, END_TIME, DETAIL, MEMO
  FROM RESERVATION
 WHERE ID = #{id}
